CREATE TABLE cliente(
    ID_CLIENTE INTEGER NOT NULL PRIMARY KEY,
    NOMBRE_CLIENTE VARCHAR(45) NOT NULL ,
    CORREO VARCHAR(40) NOT NULL ,
    TELEFONO VARCHAR(20) NOT NULL ,
    DIRECCION VARCHAR(50) NOT NULL ,
    EDAD INTEGER NOT NULL ,
    RANGO VARCHAR(20) NOT NULL 
);
CREATE TABLE creditos(
    ID_CREDITOS INTEGER NOT NULL PRIMARY KEY,
    FECHA_OTORGAMIENTO date NOT NULL ,
    CANTIDAD_CREDITO FLOAT NOT NULL ,
    VIGENCIA date NOT NULL ,
    SALDO_RESTANTE FLOAT NOT NULL ,
    INTERES DOUBLE PRECISION NOT NULL ,
    FK_ID_CLIENTE INTEGER NOT NULL,
    FK_ID_EMPLEADO INTEGER NOT NULL
);
CREATE TABLE pagos(
    ID_PAGO INTEGER NOT NULL PRIMARY KEY,
    FECHA_PAGO date NOT NULL ,
    CANTIDAD_PAGO FLOAT NOT NULL ,
    FK_ID_CLIENTE INTEGER NOT NULL,
    FK_ID_EMPLEADO INTEGER NOT NULL,
    FK_ID_CREDITO INTEGER NOT NULL
);
CREATE TABLE usuarios(
    ID_USUARIO INTEGER NOT NULL PRIMARY KEY,
    USUARIO VARCHAR(45) NOT NULL ,
    PASSWORD VARCHAR(20) NOT NULL 
);
